import React, { useEffect, useState } from 'react'
import { db } from "../firebase-config";
import styled from 'styled-components';
import moment from 'moment';
import {
    collection,
    getDocs,
    addDoc,
    updateDoc,
    deleteDoc,
    doc,
    serverTimestamp,
    query,
    orderBy
  } from "firebase/firestore";

const Appointments = () => {
    const [users, setUsers] = useState([]);
    const usersCollectionRef = collection(db, "users");

    useEffect(() => {
        const getUsers = async () => {
          const q = query(usersCollectionRef, orderBy('timestamp', 'desc'));
          const data = await getDocs(q, usersCollectionRef);
          setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id, timestamp: doc.timestamp })));
        };

        getUsers();
      }, []);



    return (
        <div>
            <h1>appointments</h1>
            {users.map((user) => {
        return (
          <div>
            {" "}
            <Info key={user.timestamp}>Name: {user.name}</Info>
            <Info key={user.timestamp}>Email: {user.email}</Info>
            <Info key={user.timestamp}>Age: {user.age}</Info>
            <Info key={user.timestamp}>Time: {moment(user.timestamp).format('MMMM Do YYYY, h:mm:ss a')}</Info>


          </div>
        );
      })}
        </div>
    )
}

export default Appointments

const Info = styled.p `
  max-width: 400px;
  font-style: normal;
  font-size: 20px;
  line-height: 140%;
  color: white;
`
