import React from "react"
import styled from "styled-components"

export default function Waves() {
  return (
    <Wrapper>
      <Background />
      <Wave
        src="images/certificate-lines.svg"
        style={{ top: "100px", filter: `blur(60px)` }}
      />
      {/* <Wave src="/images/waves/hero-wave2.svg" style={{ top: "350px" }} />
      <Wave src="/images/waves/footer-wave3.svg" style={{ top: "550px" }} /> */}
    </Wrapper>
  )
}

const Wrapper = styled.div`
  position: relative;
`

const Wave = styled.img`
  position: absolute;
  z-index: -1;
`

const Background = styled.div`
  background: linear-gradient(180deg, #013059 0%, #013059 100%);
  position: absolute;
  width: 100%;
  height: 800px;
  z-index: -1;
`
