import { useState, useEffect } from "react";
import Card from './Card'
import styled from "styled-components";
import { db } from "../firebase-config";
import moment from 'moment';
import { Link } from 'react-router-dom';
import {
  collection,
  getDocs,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  serverTimestamp,
  query,
  orderBy
} from "firebase/firestore";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Form() {
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newAge, setNewAge] = useState(0);
  const [users, setUsers] = useState([]);
  const usersCollectionRef = collection(db, "users");

  const createUser = async (e) => {
    e.preventDefault();
    if (newName.length === 0) {
      return toast.error('please enter a name!');
    }
    if (newEmail.length === 0) {
      return toast.error('please enter a valid email!');
    }
    if (newAge.length === 0) {
      return toast.error('please fill in your age!');
    } else {

      await addDoc(
        usersCollectionRef, 
        { name: newName,
          email: newEmail, 
          age: Number(newAge), 
          timestamp: serverTimestamp(),
        });
        return toast.success('appointment successfully created!');    
      }    
  };


  const updateUser = async (id, age) => {
    const userDoc = doc(db, "users", id);
    const newFields = { age: age + 1 };
    await updateDoc(userDoc, newFields);
  };

  const deleteUser = async (id) => {
    const userDoc = doc(db, "users", id);
    await deleteDoc(userDoc);
  };

  useEffect(() => {
    const getUsers = async () => {
      const q = query(usersCollectionRef, orderBy('timestamp', 'desc'));
      const data = await getDocs(q, usersCollectionRef);
      setUsers(data.docs.map((doc) => ({ ...doc.data(), id: doc.id, timestamp: doc.timestamp })));
    };

    getUsers();
  }, []);

  return (
    <FormWrapper>
      <ToastContainer />
      <HeroWrapper>
      <Card />
      <TextWrapper>
      <Title>Appointment Application</Title>
      <Caption>Build a web app with React Hooks</Caption>
      <input
          placeholder="Name..."
          onChange={(event) => {
          setNewName(event.target.value);
        }}
      />
      <input
          placeholder="Email..."
          onChange={(event) => {
          setNewEmail(event.target.value);
        }}
      />
      <input
        type="number"
        placeholder="Age..."
        onChange={(event) => {
        setNewAge(event.target.value);
        }}
      />
      <Link onClick={createUser} to='/appt'>
        <button > Create Appointment</button>
      </Link>



    </TextWrapper>
      </HeroWrapper>
    </FormWrapper>
  );
}

export default Form;

const FormWrapper = styled.div `
    max-width: 500px;
    margin: auto;
`

const HeroWrapper = styled.div `
    display: grid;
    max-width: 1234px;
    grid-template-columns: 360px auto;
    gap: 60px;
    padding: 220px 20px 0px;
    justify-content: center;
    margin: 0 auto;

    @media (max-width: 780px) {
      grid-template-columns: 1fr;
      justify-items: center;
    }
`

const TextWrapper = styled.div `
    position: relative;
    display: grid;
    gap: 20px;

    input {
      border-radius: 5px;
      padding: 5px;
    }
`


const Title = styled.h1 `
    max-width: 500px;
    font-style: normal;
    font-weight: bold;
    font-size: 60px;
    line-height: 72px;
    color: white;
    mix-blend-mode: normal;
    text-shadow: 0px 20px 40px rgba(0,0,0,0.3);
`

const Caption = styled.p `
  font-size: 15px;
  font-style: normal;
  line-height: 130%;
  text-transform: uppercase;
  color: rgba(255, 255, 255, 0.7);
`



